import React, { useState } from "react";
import MovieList from "./components/MovieList";
import Filter from "./components/Filter";

export default function App() {
  const [movie, setMovie] = useState([
    {
      title: "Inception",
      description:
        "A skilled thief enters the dreams of others to steal secrets or plant ideas.",
      posterURL:
        "https://m.media-amazon.com/images/I/91ebheNmoUL._AC_SL1500_.jpg",
      rating: 8.8,
    },
    {
      title: "The Dark Knight",
      description: "Batman faces the Joker, who wreaks havoc on Gotham City.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 7.9,
    },
    {
      title: "Interstellar",
      description:
        "A team of explorers travels through a wormhole to save humanity.",
      posterURL:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9903HwMwCBV6MZmXu2mWfqYvmOFoUF4RRS1asTqSSnwNdce-0rRWc-YwoTLE5YznkTIGEpuk&s=10",
      rating: 4.6,
    },
    {
      title: "Titanic",
      description: "A love story unfolds aboard the ill-fated RMS Titanic.",
      posterURL:
        "https://m.media-amazon.com/images/I/71DEh6crcqL._UF1000,1000_QL80_.jpg",
      rating: 7.8,
    },
    {
      title: "The Shawshank Redemption",
      description:
        "Two imprisoned men bond over years, finding hope and redemption.",
      posterURL: "https://m.media-amazon.com/images/I/51NiGlapXlL._AC_.jpg",
      rating: 9.3,
    },
    {
      title: "Avengers: Endgame",
      description:
        "The Avengers assemble to undo the devastating effects of Thanosâ€™ snap.",
      posterURL:
        "https://m.media-amazon.com/images/I/81ExhpBEbHL._AC_SY679_.jpg",
      rating: 2.4,
    },
    {
      title: "The Matrix",
      description:
        "A hacker discovers reality is a simulation and joins the fight for freedom.",
      posterURL: "https://m.media-amazon.com/images/I/51EG732BV3L._AC_.jpg",
      rating: 6.7,
    },
    {
      title: "Forrest Gump",
      description:
        "The life journey of a simple man with extraordinary experiences.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 8.8,
    },
    {
      title: "Gladiator",
      description:
        "A betrayed Roman general seeks vengeance in the gladiatorial arena.",
      posterURL: "https://m.media-amazon.com/images/I/51A5cHQhSqL._AC_.jpg",
      rating: 9.5,
    },
    {
      title: "Jurassic Park",
      description:
        "Scientists clone dinosaurs for a theme park, but chaos ensues.",
      posterURL:
        "data:image/jpeg;base64,",
      rating: 4.1,
    },
  ]);

  const [favmovies, setFavmovies] = useState([]);
  const [search, setSearch] = useState("");
  const [rating, setRating] = useState(0);
  const [view, setView] = useState("search");

  const handleFav = (movieItem) => {
    setFavmovies((prev) =>
      prev.some((m) => m.title === movieItem.title)
        ? prev.filter((m) => m.title !== movieItem.title)
        : [...prev, movieItem]
    );
  };

  const filteredMovies = movie.filter(
    (m) =>
      m.title.toLowerCase().includes(search.toLowerCase()) && m.rating >= rating
  );

  return (
    <div className="container mx-auto">
      <p className="text-red-700 text-2xl font-extrabold ml-4 mt-4">
        MovieFlix
      </p>

      <div className="tabs tabs-border mx-auto mb-6 flex gap-4 justify-center">
        <a
          className={`tab text-lg ${view === "search" ? "tab-active" : ""}`}
          onClick={() => setView("search")}
        >
          Search / Popular
        </a>

        <a
          className={`tab text-lg ${view === "favorites" ? "tab-active" : ""}`}
          onClick={() => setView("favorites")}
        >
          Favorites
        </a>
      </div>

      {view === "search" && (
        <Filter
          search={search}
          setSearch={setSearch}
          rating={rating}
          setRating={setRating}
        />
      )}

      {view === "search" ? (
        <MovieList
          movies={filteredMovies}
          onFav={handleFav}
          favmovies={favmovies}
        />
      ) : (
        <MovieList movies={favmovies} onFav={handleFav} favmovies={favmovies} />
      )}
    </div>
  );
}
